import React, { Component } from 'react';
import Square from './Square'
class Board extends Component {
    constructor(props) {
        super(props)

        //define the array array that the user start with
          this.state = {
              array: ["", "", "", "", "", "", "", "", ""],
              userStatus: "",
              mark: "⭕️"
        }
    }

    handleLocation = (index) => {
         let {array, userStatus, mark} = this.state
         if(mark === "⭕️"){
             this.setState({mark: "❌"})
         } else if (mark === "❌"){
             this.setState({mark: "⭕️"})
         }
         array[index] = mark
    }

//Resetting the Game
    resetGame = () => {
        let {array,userStatus, mark} = this.state
        array = array.map(value => value = "")
        this.setState({
            array: array,
            userStatus: userStatus,
            mark: "⭕️"
        })
    }


  render() {
    let { array, userStatus } = this.state
    let square = array.map((value, index) => {
        return (
        <Square
        handleLocation = {this.handleLocation}
        index = { index }
        value = { value }
        userStatus = {userStatus}
        /> )})



    return (
    <>
    <br/>
     <div className = "board">
        {square}
        <button onClick = {this.resetGame}> Reset Game</button>
        <br/>
        <p>{this.state.userStatus}</p>
      </div>
      </>
    );
  }
}

export default Board
